import React, { Component } from 'react';

class Splash extends Component {
	constructor(props) {
		super(props);
		this.state = { event_name: '' };
	}

	handleSubmit(event) {
    	this.setState({event_name: event.target.event_name});
    	console.log(this.event_name)
  	}

	render() {
		return (
			<div className="App">
			 <header className="App-header">
				<form onSubmit={this.handleSubmit}>
				    <div className="col">
				      <input type="text" className="form-control" placeholder="Event Name"  value={this.state.event_name} />
				    </div>
				</form>
				<div> 
				<label>
				Repeating: <input type="checkbox" data-toggle="toggle" data-size="mini" data-offstyle="info"/>
				</label>
				</div>
			 </header>
			</div>
		);
	}
}

export default Splash;
