import React, { Component } from 'react';

class Splash extends Component {
	constructor(props) {
		super(props);
	this.state = {event_name: ''};
    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({event_name: event.target.event_name});
  }

  handleSubmit(event) {
    alert('A name was submitted: ' + this.state.event_name);
    event.preventDefault();
  }

  render() {
    return (
     <header className="App-header">
      <form onSubmit={this.handleSubmit}>
        <label>
          Name:
          <input type="text" event_name={this.state.event_name} onChange={this.handleChange} />
        </label>
        <input type="submit" event_name="Submit" />
      </form>
      </header>
    );
	}
}

export default Splash;
