import React, { Component } from "react";
import { Checkbox} from "@material-ui/core";
import { Field, Formik } from "formik";

const API_LINK = "http://localhost:8000/api/"

class Splash extends Component {

  async save_event (values) {
    fetch(API_LINK + "post", {
      method: "POST",
      body: JSON.stringify(values, null, 2)
    })
  }

  render() {
    return (
      <Formik
        initialValues={{
          event_name: "",
          timezone: "",
          earliest: "",
          latest: "",
          repeating: false,
        }}
        onSubmit={(values) => {
          console.log(values)
          save_event(values)
          alert(JSON.stringify(values, null, 2));
        }}>
        { (props) => (
          <div className="App">
            <div className="App-header">
              <form onSubmit = {props.handleSubmit}>
                <label>Event Name</label>
                <input
                  id="event_name"
                  name="event_name"
                  type="event_name"
                  className="form-control"
                  value={props.values.event_name}
                  onChange={props.handleChange}
                />
                <Field
                  name="earliest"
                  as="select"
                  placeholder="At the earliest"
                  className="form-control"
                  value={props.values.earliest}
                  onChange={props.handleChange}
                >
                  <option defaultValue>At the earliest</option>
                  <option value="12">12</option>
                  <option value="2">2</option>
                </Field>
                <Field
                  name="latest"
                  as="select"
                  placeholder="At the latest"
                  className="form-control"
                  value={props.values.latest}
                  onChange={props.handleChange}
                >
                  <option defaultValue>At the latest</option>
                  <option value="12">12</option>
                  <option value="2">2</option>
                </Field>
                <Field
                  name="timezone"
                  as="select"
                  placeholder="Timezone"
                  className="form-control"
                  value={props.values.timezone}
                  onChange={props.handleChange}
                >
                  <option defaultValue>Timezone</option>
                  <option value="EST">EST</option>
                  <option value="PST">PST</option>
                </Field>
                <label><small>Repeating</small></label>
                <Checkbox
                  name="repeating"
                  type="checkbox"
                  checked={props.values.repeating}
                  onChange={props.handleChange}
                ></Checkbox>
                <div>
                  <button type="submit" className="form-control">
                    Create Event
                  </button>
                </div>
              </form>
            </div>
          </div>
        )}
      </Formik>
    );
  }
}

export default Splash;
