import React, { Component } from 'react';

class Splash extends Component {
	constructor(props) {
		super(props);
		this.state = { event_name: 'Event name' };
		this.handleChange = this.handleChange.bind(this);
	    this.handleSubmit = this.handleSubmit.bind(this);
	}

	handleSubmit = () => {
		alert('A name was submitted: ' + this.state.event_name);
		e.preventDefault();
  	}
  	handleChange = () => {
    	this.setState({event_name: e.target.event_name});
    	console.log(this.event_name)
  	}

	render() {
		return (
			<div className="App">
			 <header className="App-header">
				<form onSubmit={this.handleSubmit}>
			      <input type="text" className="form-control" value={this.state.event_name} onChange={this.handleChange}/>
				</form>
				<div> 
				<label>
				Repeating: <input type="checkbox" data-toggle="toggle" data-size="mini" data-offstyle="info"/>
				</label>
				</div>
			 </header>
			</div>
		);
	}
}

export default Splash;
