import React, { Component } from 'react';
import  { Field, useFormik, Formik } from 'formik';

const EventInfo = () => {
  const event_info = useFormik({
    initialValues: {
      event_name: '',
      color: '',
      timezone: '',
    },
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
    },
  });

  return (
    <div>
    <Formik>
    <form onSubmit={event_info.handleSubmit}>
      <label>Event Name</label>
      <input
        id="event_name"
        name="event_name"
        type="event_name"
        className="form-control"
        onChange={event_info.handleChange}
        value={event_info.values.event_name}
      />
    <Field
      name="timezone" 
      as="select"
      onChange={event_info.handleChange}
      value={event_info.values.timezone} 
      placeholder="Timezone"
      className="form-control">   
         <option value="EST">EST</option>
         <option value="PST">PST</option>
    </Field>
    <div>
    	<button type="submit">Create Event</button>
    </div>
    </form>
    </Formik>
    </div>
  );
};


class Splash extends Component {
	render() {
		return(
			<div  className="App">
			<header className="App-header"> 
			<EventInfo />
			<div> 
				Repeating: <input type="checkbox" data-toggle="toggle" data-size="mini" data-offstyle="info"/>
			</div> 
			</header>
			</div>
		)
	}
}

export default Splash
