import React, { Component } from 'react';
import  { useFormik } from 'formik';

const SignupForm = () => {
  const event_info = useFormik({
    initialValues: {
      event_name: '',
      color: '',
    },
    onSubmit: values => {
      alert(JSON.stringify(values, null, 2));
    },
  });
  return (
    <form onSubmit={event_info.handleSubmit}>
      <label>Event Name</label>
      <input
        id="event_name"
        name="event_name"
        type="event_name"
        className="form-control"
        onChange={event_info.handleChange}
        value={event_info.values.event_name}
      />

	<select as="select" name="color">
		<option value="red">Red</option>
		<option value="green">Green</option>
		<option value="blue">Blue</option>
	</select>

    </form>
  );
};


class Splash extends Component {
	render() {
		return(
			<div  className="App">
			<header className="App-header"> 
			<SignupForm />
			<div> 
				Repeating: <input type="checkbox" data-toggle="toggle" data-size="mini" data-offstyle="info"/>
			</div> 
			</header>
			</div>
		)
	}
}

export default Splash
